#include <cstdio>#include <cstring>#include <cstdlib>#include <iostream>#include <algorithm>using namespace std;int main(int argc, char **argv) {    int array[10010], n;    long long ans;    while(~scanf("%d", &n)) {        ans = 0;        for(int i = 0; i < n; ++i) {            scanf("%d", &array[i]);        }        sort(array, array+n);        for(int i = 0; i < n; ++i) {            ans += (n-i) * array[i];        }        printf("%lld\n", ans);    }    return 0;}