#include <cstdio>#include <cstring>#include <cstdlib>#include <iostream>using namespace std;struct Ele {    int a, b;    double x;} e[1005];int cmp(const void *a, const void *b) {    Ele *x = (Ele *)a, *y = (Ele *)b;    if(x->x != y->x) {        return x->x < y->x;    }    else {        return x->a > y->a;    }}int main(int argc, char **argv) {    int n, k;    while(~scanf("%d %d", &n, &k)) {        for(int i = 0; i < n; ++i) {            scanf("%d", &e[i].a);        }        for(int i = 0; i < n; ++i) {            scanf("%d", &e[i].b);            e[i].x = e[i].b * 1.0 / e[i].a;        }        qsort(e, n, sizeof(e[0]), cmp);        int sum = 0, ans = 0;        for(int i = 0; i < n; ++i) {            if(sum + e[i].a <= k) {                sum += e[i].a;                ans += e[i].b;            }            else {                break;            }        }        printf("%d\n", ans);    }    return 0;}