#include <cstdio>#include <cstring>#include <cstdlib>#include <iostream>using namespace std;struct Ele {    int a, b, index;    double x;} e[1005];int cmp(const void *a, const void *b) {    Ele *x = (Ele *)a, *y = (Ele *)b;    if(x->x != y->x) {        return x->x < y->x;    }    else {        return x->a > y->a;    }}int main(int argc, char **argv) {    int n;    while(~scanf("%d", &n)) {        for(int i = 0; i < n; ++i) {            scanf("%d", &e[i].a);        }        for(int i = 0; i < n; ++i) {            scanf("%d", &e[i].b);            e[i].x = e[i].a * 1.0 / e[i].b;            e[i].index = i + 1;        }        qsort(e, n, sizeof(e[0]), cmp);        printf("%d\n", e[1].index);    }    return 0;}