#include <cstdio>using namespace std;int sum[100001] = {0};int cp[100001] = {0};void init() {    for(int i = 0; i < 100001; ++i) {        sum[i] += i;        int tmp = i;        while(tmp) {            sum[i] += (tmp % 10);            tmp /= 10;        }    }    for(int i = 0; i < 100001; ++i) {        cp[i] = -1;    }    for(int i = 0; i < 100001; ++i) {        if(cp[sum[i]] == -1 ) {            cp[sum[i]] = i;        }    }}int main(int argc, char **argv) {    freopen("data.in", "r", stdin);     int n = 0;    init();    while(~(scanf("%d", &n))) {        if(cp[n] == -1) {            printf("0\n");        }        else {            printf("%d\n", cp[n]);            }    }    return 0;}