#include <cstdio>using namespace std;int main(int argc, char **argv) {    freopen("data.in", "r", stdin);     int T;    long long n, k, ans = 0, c;    scanf("%d", &T);    while(T--) {        ans = 0;        scanf("%lld", &n);        c = n * (n + 1) / 2;        for(long long i = 1; i <= n; ++i) {            scanf("%lld", &k);            ans += (i * k * (n+1-i));        }        printf("%.3lf\n", ans * 1.0 / c);    }    return 0;}