#include <cstdio>#include <cstring>using namespace std;int map[501][501] = {{0}};int vis[501] = {0};int match[501] = {0};int n, m, k, sum;bool dfs(int u) {    for(int v = 1; v <= m; ++v) {        if(!vis[v] && map[u][v]) {            vis[v] = true;            if(match[v] == 0 || dfs(match[v])) {                match[v] = u;                return true;            }        }    }    return false;}int main(int argc, char **argv) {    freopen("data.in", "r", stdin);     while(~scanf("%d %d %d", &n, &m, &k)) {        memset(map, 0, sizeof(map));        memset(match, 0, sizeof(match));        int x, y;        sum = 0;        for(int i = 0; i < k; ++i) {            scanf("%d %d", &x, &y);            map[x][y] = 1;        }        for(int i = 1; i <= n; ++i) {            memset(vis, 0, sizeof(vis));            if(dfs(i)) {                sum++;            }        }        printf("%d\n", sum);    }    return 0;}