#include <cstdio>#include <cstring>#include <iostream>using namespace std;int a[1000010], q[1000010];// 单调队列int main(int argc, char **argv) {    freopen("data.in", "r", stdin);     int t, n, m;    scanf("%d", &t);    while(t--) {        scanf("%d %d", &n, &m);        for(int i = 0; i < n; ++i) {            scanf("%d", &a[i]);        }        if(n == 0) {            printf("0\n");            continue;        }        int head = 0, tail = 0, ans = 0;        q[tail++] = a[0];        ans = max(ans, 1+m);        for(int i = 1; i < n; ++i) {            q[tail++] = a[i];            while(q[tail-1]-q[head]+1-(tail-1-head+1) > m) {                head++;            }            ans = max(ans, q[tail-1]-q[head]+1+m-                    (q[tail-1]-q[head]-(tail-1-head)));        }        printf("%d\n", ans);    }    return 0;}