#include <cstdio>#include <cstring>using namespace std;int left[1000010], right[1000010];int a[1000010];inline int max(int a, int b) {    return a > b ? a: b;}inline int min(int a, int b) {    return a < b ? a : b;}int main(int argc, char **argv) {    freopen("data.in", "r", stdin);     int t, n;    long long ans;    scanf("%d", &t);    while(t--) {        ans = 0;        memset(a, 0, sizeof(a));        memset(left, 0, sizeof(left));        memset(right, 0, sizeof(right));        scanf("%d", &n);        for(int i = 1; i <= n; ++i) {            scanf("%d", &a[i]);            left[i] = max(left[i-1], a[i-1]);        }        for(int i = n; i >= 1; --i) {            right[i] = max(right[i+1], a[i+1]);        }        for(int i = 1; i <= n; ++i) {            if(min(left[i], right[i]) > a[i]) {                ans += (min(right[i], left[i]) - a[i]);            }        }        printf("%lld\n", ans);    }    return 0;}