#include <cstdio>#include <cstring>using namespace std;long long array[105][105];void init() {    array[1][1] = 1;    for(int i = 2; i <= 101; ++i) {        for(int j = 1; j <= i; ++j) {            array[i][j] = array[i-1][j-1] + array[i-1][j];        }    }}int main(int argc, char **argv) {    int n, k;    int x, y;    init();    while(~scanf("%d %d", &n, &k)) {        while(k--) {            scanf("%d %d", &x, &y);            if(x >= 0 && y >= 0 && x < 105 && y < 105 && x >= y) {                printf("%lld\n", array[x][y] == 0 ? -1 : array[x][y]);            }            else {                printf("-1\n");            }        }    }    return 0;}