#include <cstdio>#include <cstring>#include <cstdlib>#include <iostream>using namespace std;void buddle_sort(int a[], int n) {    int cnt = 0, tmp;    for(int j = 0; j < n - 1; ++j) {        for(int i = 0; i < n-1-j; ++i) {            if(a[i] < a[i+1]) {                cnt++;                tmp = a[i];                a[i] = a[i+1];                a[i+1] = tmp;            }        }    }    for(int i = 0; i < n; ++i) {        printf("%d ", a[i]);    }    printf("\n%d\n", cnt);}int main(int argc, char **argv) {    int n;    int a[1005];    while(~scanf("%d", &n)) {        for(int i = 0; i < n; ++i) {            scanf("%d", &a[i]);        }        buddle_sort(a, n);    }    return 0;}